    try {
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/tenants/${tenantId}/reset-password`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          newPassword
        })
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to reset password');
      }
      const data = await response.json();
      alert(`âœ… Password reset successfully!\n\n` + `Username/Email: ${data.username || email}\n` + `New Password: ${newPassword}\n\n` + `âš ï¸ IMPORTANT: The tenant must login using their EMAIL as the username.\n` + `Please share these credentials with the tenant securely.`);
